float Hash21(vec2 p)
{
 	p = fract(p*vec2(iMouse.x + 55.24565, iMouse.y/0.568 + 435.345));
    p += dot(p, p+34.23);
    return fract(p.x*p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord- .5 * iResolution.xy)/iResolution.y;


    vec3 col = vec3(0);

    uv *= 5.;

    vec2 gv = fract(uv) - .5;
    vec2 id = floor(uv);

    float n = Hash21(id); // return random num 0-1
    float width = .04*abs(uv.y+2.);

    if (n<.5) gv.x *= -1.;
    float d = abs(abs(gv.x + gv.y)-.5);
    vec2 cUv = gv-sign(gv.x+gv.y+.001)*.5;
    d = length(cUv);
    float mask = smoothstep(.01, -.01, abs(d-.5) - width);
    //uv += iTime*iMouse.x/100.;

    float angle = atan(cUv.x, cUv.y); // -pi to pi
    float checker = mod(id.x+id.y, 2.)*2.-1.;
    float flow = sin(iTime + angle*checker * 10.);
    float a = iTime*10. + angle*checker * 10.;
    col += (sin(a)*mask)/4.;
    float x = fract(angle/1.57);
    float y = (d-(.5-width))/(2.*width);
    y = abs(y-.5)*2.;
    vec2 tUv = vec2(x,y);
    col.rg += tUv*mask;

    //col += checker;
    //col += n;

    //if (gv.x > .48 || gv.y > .48) col = vec3(1,0,0);

    fragColor = vec4(col,1.0);
}
