void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float speed = 1.0;
    float cosa = cos(iTime * speed);
    float sina = sin(iTime * speed);
    float pivot = 0.5;

    uv -= pivot;
    uv = vec2(
        (cosa * uv.x - sina * uv.y),
        (cosa * uv.y + sina * uv.x)
    ) + pivot;

    vec4 tex = texture(iChannel0,uv);

    // Output to screen
    fragColor = tex;
}
