void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    //vec2 uv = fragCoord/iChannelResolution[0].xy;
    vec2 uv2 = fragCoord.xx;
    vec2 uv = fragCoord/iResolution.xy;
    //uv.x += 3.0*iMouse.x*texture(iChannel1,uv.yy).r/iResolution.y;
    float offset = (-0.5+2.0*iMouse.x/iResolution.x);
    float glitch = texture(iChannel2,uv.yy+vec2(0.0,iTime*0.4)).r;
        float rad180=radians(180.0);
    if(offset>1.0)offset=1.0;
    else if(offset<0.0)offset=0.0;
        float curva = sin(rad180*offset)*glitch;
    offset+=curva;
    if(offset>1.0)offset=1.0;
    else if(offset<0.0)offset=0.0;
        uv.x+=offset;

    // Output to screen
    //fragColor=vec4(curva,curva,curva,1.0);
    if(uv.x<1.0)fragColor = texture(iChannel0,uv);
    else fragColor = texture(iChannel1,uv-vec2(1.0,0.0));
}
