vec2 z;
vec2 w;

void mainImage(out vec4 o, vec2 p)
{	
    // Original
    // TouchDesigner didn't like the division assignments on one line
    //p.y /= iResolution.y;
    //p.y += tan(iTime * 0.2 + tan(p.x) + sin(.2 * p.x));
    //o = vec4(0., .3 + (p.y < 0. ? 0. : 1. - p.y * 3.), 0., 1.);
	//o = vec4(.4 + (p.y < 0. ? 0. : 1. - p.y * 5.), 
    //         .3 + (p.y < 0. ? 0. : 1. - p.y * 5.), 
    //         0.,
    //        1.);

    z.y = p.y / iResolution.y;
    z.x = p.x;

    w.y = z.y + tan(iTime * 0.2 + tan(z.x) + sin(.2 * z.x));
    w.x = z.x;


	o = vec4(.4 + (w.y < 0. ? 0. : 1. - w.y * 5.), 
             .3 + (w.y < 0. ? 0. : 1. - w.y * 5.), 
             0.,
             1.);
}
