/**
// old version (159)

#define n iChannel0
#define r iResolution.xy
#define t texture
#define mainImage(c, p)                      \
	vec2 u = p.xy/r;                         \
    vec4 k = t(n, r),                        \
         a = t(n, u),                        \
         b = t(iChannel1, u-(a.rb-k.rb)*.1); \
    c = b;

/**/

////////////////////////////////////////////////////////////////////////////////////////////////////
//
// fixed version (138 chars) and inspired by the comments 
//
////////////////////////////////////////////////////////////////////////////////////////////////////

#define t(j) texture ( iChannel0, j ).xy  
void mainImage( out vec4 c, in vec2 p ) 
{
    vec2 r = iResolution.xy;
    c = texture( iChannel1, p/r - (  t(p/r) - t(r)  ) * .1 );
}
