void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy/iResolution.xy;
    vec2 uva = uv - .5;
    uva.x *= iResolution.x/iResolution.y;

    vec3 rgb = vec3(sin(iTime+uva.x-0.4),sin(iTime+cos(uva.y)),cos(iTime+1.0+uva.y))+0.6;

    float radius = abs(cos(iTime))*0.4 - 0.03;

    if (iMouse.y >= iResolution.y/2.0) {
        radius = 0.3;
    }

    float coloffset = 0.1;
    vec2 move = vec2(sin(iTime*2.0)*0.25,0);



    vec3 result = circle(radius,uva,rgb,coloffset,move);


    // Output to screen
    fragColor = vec4(result,1.0);
}

vec3 circle( float r, vec2 uv, vec3 color, float coloffset, vec2 offset)
{
    float d = length(uv - offset);

    float c = smoothstep(r,r-0.002,d) + clamp(coloffset,0.0,1.0);

    return c*color;


}
