//Discrete Fermat's spiral

#define t iTime
#define res iResolution.xy

const float n = 100.; // number of lights
const float r = 0.014; // radius of lights
const float R = 0.08; // radius of the innermost orbit
const float rot = 0.01; // speed of rotation
const float colChange = 0.2; // rate of color change
const float aveBright = 0.75; // average brightness
const float PI  = 3.141592653589793;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy * 2. - res) / min(res.x, res.y);
    vec3 destColor = vec3(0.);

    vec3 aveLight = vec3(aveBright); // average light
    float amp = 1. - aveBright; // amplitude

    float a = colChange * t;
    float b = 1.1 * colChange * t + 2./3. * PI;
    float c = 1.2 * colChange * t + 4./3. * PI;

    vec3 innerColor = vec3(sin(a), sin(b), sin(c)) * amp + aveLight;
    vec3 outerColor = vec3(sin(b), sin(c), sin(a)) * amp + aveLight;

    for(float i = 1.; i < n+1.; i++){

        float d = t * i * rot;
        vec2 q = p + vec2(cos(d), sin(d)) * sqrt(i) * R;
        vec3 mixedColor = mix(innerColor, outerColor, i/n);

        destColor += mixedColor * r * r / dot(q, q);
    }
    fragColor = vec4(destColor, 1.);
}
