float Hash21(vec2 p){
    p = fract(p*vec2(234.34, 435.345));
    p += dot(p, p+34.23);
    return fract(p.x*p.y);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.xy;
    vec2 UV = fragCoord.xy/iResolution.xy;

    vec3 col = vec3(0);
    //uv += iTime*.02;
    uv *= 10.;
    vec2 gv = fract(uv) - .5;
    vec2 id = floor(uv);
    float width = .4*UV.y;
    float n = Hash21(id); //random number 0 - 1;

    if (n<.5) gv.x *= -1.;
    //float d = abs(abs(gv.y+gv.x)-.5); // straight lines like maze
    vec2 cUv = gv-sign(gv.y+gv.x+.001)*.5;
    float d = length(cUv);
    float mask = smoothstep(.01,-.01, abs(d-.5)-width);
    float angle = atan(cUv.x, cUv.y);
    float checker = mod(id.x+id.y, 2.)*2.-1.;
    float flow = sin(iTime+checker*angle*10.);

    float x = checker*angle/1.57-iTime*.3;
    float y = (d-(.5-width))/(2.*width);
 	y = abs(y-.5)*2.;
    vec2 tUv = vec2(x,y);
    col += texture(iChannel0, tUv).rgb*mask;
    col *= 1.-tUv.y;

    //col += n;
	//col += mask;

    //if (gv.x > .48 || gv.y>.48) col = vec3(1,0,0);

    fragColor = vec4(col,1.0);
}
