float circle(vec2 uv, vec2 center, float min, float max)
{
    return (1. - smoothstep(min, max, length(uv - center)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-0.*iResolution.xy)/iResolution.y;
    uv.y -= 0.5;


    vec2 polar = vec2(length(uv), atan(uv.x / uv.y) * 1.14);
    //uv = polar;


    uv *= 15.;

    vec3 col = vec3(0.);

    float numCircles = cos(iTime) * 50.  + 55.;

    for(float i = 0.; i < numCircles; i = i + 1.){
          vec2 temp = uv;
          temp.x -= i*0.7 + 5.;
          vec3 color = vec3(0.8, 0.6, i / numCircles);
          float circ = circle(temp, vec2(sin(iTime * 7.1 - i*0.3), cos(iTime * 6. - i*0.3)), 0.1, 0.13);
          col += circ * color;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
