void mainImage( out vec4 O, vec2 u )
{   vec2  U = u / iResolution.xy;                       // normalized coordinates
    float z = 1.-.7*U.y;                                // depth along the plane
        O = 1.0*texture(iChannel0, 
                       vec2( U.x-.5 , 1 )/ z -.1*iTime // perspective parameterization
                       ).xxxx;
    O.w = 1./z;
}
