#ifdef GL_ES
precision mediump float;
#endif

#define PI 3.14159265359

uniform vec2 u_resolution;
uniform vec2 u_mouse;
uniform float u_time;


vec3 colorA = vec3(0.137,0.933,1.000); //light blue
vec3 colorB = vec3(0.179,0.303,0.675); //dark blue



void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
    vec2 st = fragCoord.xy/iResolution.xy; //normalize fragcoord
    vec3 color = vec3(0.0);

    vec3 sky = vec3(pow(st.y,1.75)); //sky calc

    float sun = (1.0-min(0.0,(st.y+sin(iTime/4.0)))-1.0)*(sin(st.x*PI)*.85+1.0); //sun calc

    // pct.g = sin(st.x*PI);
    // pct.b = pow(st.x,0.5);

    color = mix(colorA, colorB, sky); //draw sky
    color = mix(color,vec3(1.000,0.417,0.379),sun); //draw sun


    fragColor = vec4(color,1.0);
}
