void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 u = 68.*fragCoord/iResolution.y;
    u *= vec2(.866,1.);
    u -= u.yx*vec2(.5,0.);

    vec2 a = fract(u);
    vec4 b = abs(a.xyxy-vec4(1.,0.,0.,1.));
    vec4 c = vec4(0.);

	c += texture(iChannel0,(u+a.yx       )/64.)*abs(a.x+a.y-1.);
    c += texture(iChannel0,(u+vec2(1.,0.))/64.)*min(b.z,b.w);
    c += texture(iChannel0,(u+vec2(0.,1.))/64.)*min(b.x,b.y);

    fragColor = c;
}
