void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;


    vec3 col = vec3(0);

 	vec3 ro = vec3(0);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));
    float t = sin(uv.x*10.+uv.y*sin(iTime));

    float dist = length(ro + rd*t);

    vec3 intensity = vec3(1. - dist);

    vec3 tint = vec3(abs(sin(fragCoord.x)),abs(cos(fragCoord.x*fragCoord.y)),0);

    col = mix(intensity, tint, .5); 

    // Output to screen
    fragColor = vec4(col,1.0);
}
