// Geometry Images: encoding mesh & bump in texture. From http://hhoppe.com/proj/gim/
// mesh image :               https://i.imgur.com/H5YzLhr.png
// bump image (unused here) : https://i.imgur.com/HE34SCq.png
// you can load them in shadertoy either from the JS console ( press F12 )
//      window.tab1.click(); gShaderToy.SetTexture(1,{mSrc:'https://i.imgur.com/H5YzLhr.png',mType:'texture',mID:1,mSampler:{filter:'mipmap',wrap:'repeat',vflip:'true',srgb:'false',internal:'byte'}});
// or by clicking the magic link in shader description.

#define LOD 1 // Compatible with MIPmapping and compression ! 

void mainImage( out vec4 O, vec2 U )
{
    O = texelFetch(iChannel0,ivec2(U), 0);
    U *= 256./iResolution.y;
    if (max(U.x,U.y)>256.) return;

    float eps = exp2(float(LOD)) / 256., 
          //z = fract(iTime);
            z = fract(float(iFrame<<LOD)/256.);

    for(int y = 0; y < 1+256>>LOD; y++)
        for(int x = 0; x < 1+256>>LOD; x++) {
            vec3 P = texelFetch(iChannel1,ivec2(x,y), LOD).xyz;
            vec2 V = P.xy - U/257.;
            if (abs(P.z-z)<eps && dot(V,V)<eps*eps) { O = vec4(z); return; }
        }
}
