// Grid noise displacement. Created by Philippe Gauthier 2019
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.


float N21(vec2 v){
	return fract(cos(v.x*753.+v.y*59.32)*652.);
}

vec2 N22(vec2 v){
	return vec2(
        fract(cos(v.x*753.+v.y*59.32)*652.)
        ,fract(cos(v.x*253.+v.y*959.32)*952.));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 UV = fragCoord/iResolution.xy;
    vec2 uv = UV;
    uv-=.5;
    uv.x *= iResolution.x/iResolution.y;

    float zoom = 4.+cos(iTime)*.5;
    vec2 guv = fract(uv*zoom);
    vec2 gid = floor(uv*zoom);

    vec2 p = N22(gid);
	vec3 col;
    col = vec3(1);
    col *= texture(iChannel0, UV+p*.07*cos(iTime*(.5*gid+.5))).rgb;

    float mask = 1.-smoothstep(0.,1., clamp(length(guv-.5)-.1,0.,1.));
    col.grb *= vec3(mask);
    //col.gr += smoothstep(0.01,.0, abs(guv));// show grid
    //col.rg += N21(gid)*.5;// show noise per cell

    // Output to screen
    fragColor = vec4(col,1.0);
}
