//dynamically generated noise
//a tutorial from "Art of Code": https://www.youtube.com/watch?v=zXsWftRdsvU&t=853s

float Noise(vec2 point){

	return fract( sin(point.x *100. + point.y *6574.)*5647.);    
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	float c = Noise(uv);
    vec3 col = vec3(c);

    fragColor = vec4(col, 1.0);
}
