void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	float t = iTime;
	vec2 r = iResolution.xy;
	vec2 uv = fragCoord.xy - r / 2.0;
	uv = vec2(length(uv) / r.y - 0.3, atan(uv.x, uv.y));    
	vec3 s = cos(5.0 * vec3(0, 1, 2) + t + uv.y + sin(uv.y) * cos(t)) / 10.0;
	vec3 e = s.zxy;
 	vec3 f = max(uv.x - s, e - uv.x);
 	fragColor.rgb = dot(clamp(f * r.y, 0., 1.), 50. * (s - e)) * (s - .1) + f;
}
