#define ROTOZOOM

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv;


    uv.x = fragCoord.x/iResolution.x;
    uv.y = fragCoord.y/iResolution.y;

    uv.x -= 0.5;
    uv.y -= 0.5;

    // set Image aspect to square or you will get an oval
    uv *= iResolution.xy  / iResolution.y;

	float zoom = sin(iTime/10.0)*10.0; 

    float u = uv.x*256.0;
    float v = uv.y*256.0;

    #ifdef ROTOZOOM
    u = uv.x*cos(iTime/2.0)*zoom+uv.y*(-sin(iTime/2.0))*zoom;
    v = uv.x*sin(iTime/2.0)*zoom+uv.y*cos(iTime/2.0)*zoom;
    #endif

    float xorpattern = float(
        int(abs(u) + 0.5)
        ^
        int(abs(v) + 0.5)
    );

	vec3 color1 = vec3(
        0.0, 
        xorpattern/10.0,  
        xorpattern/50.0

    );

	fragColor = vec4(color1, 1.0);

}
