void mainImage( out vec4 Q, in vec2 U )
{
    vec4 a = A(U);
    float l = ln(U,a.xy,a.zw);
    Q = sin(11.32+.25*l+vec4(1,2,3,4))*smoothstep(1.,4.,l);

}

#define R iResolution.xy
//#define A(U) texelFetch(iChannel0,ivec2(U),0)
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define C(U) texture(iChannel2,(U)/R)
#define D(U) texture(iChannel3,(U)/R)

#define I 25.

#define f1 15.
#define f2 1.4
#define std vec4(8,2,1,1)

float ln (vec2 p, vec2 a, vec2 b) {
	return length(p-a-(b-a)*clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.));
}

void X (inout vec4 Q, vec2 U, vec2 r) {
    vec4 n = A(U+r);
	if (ln(U,n.xy,n.zw)<ln(U,Q.xy,Q.zw)) Q = n;
}
void mainImage( out vec4 Q, in vec2 U )
{
    Q = A(U);

    X(Q,U,vec2(1,0));
    X(Q,U,vec2(0,1));
    X(Q,U,vec2(-1,0));
    X(Q,U,vec2(0,-1));


    Q.xy -= D(Q.xy).zw;
    Q.zw -= D(Q.zw).zw;

    if (length(Q.xy-Q.zw) > 2.) {
        vec2 m = 0.5*(Q.xy+Q.zw);
        if (length(U-Q.xy) > length(U-Q.zw)) 
        	Q.xy = m;
        else Q.zw = m;
    }
    vec4 
        n = A(U+vec2(0,1)),
        e = A(U+vec2(1,0)),
        s = A(U-vec2(0,1)),
        w = A(U-vec2(1,0));
    vec2 g = vec2(e.x-w);

    if (iMouse.z>0.) {
        vec4 n = B(vec2(0));
    	if (ln(U,n.xy,n.zw)<ln(U,Q.xy,Q.zw)) Q = n;
    }

    if (iFrame < 1) {
    	Q = vec4(-R,-R);
    }
}

//Mouse
void mainImage( out vec4 C, in vec2 U )
{
    vec4 p = texture(iChannel0,U/iResolution.xy);
   	if (iMouse.z>0.) {
      if (p.z>0.) C =  vec4(iMouse.xy,p.xy);
    else C =  vec4(iMouse.xy,iMouse.xy);
   }
    else C = vec4(-iResolution.xy,-iResolution.xy);
}

void mainImage( out vec4 Q, in vec2 U )
{
    Q = vec4(0);
    for (float i = -I; i <= I; i++) {
        vec2 x = U+vec2(i,0);
        vec4 b = A(x);
        float l = ln(x,b.xy,b.zw);
        float s = smoothstep(2.,1.,l);
    	Q += s*0.3989422804/std*exp(-.5*i*i/std/std);
    }
}

void mainImage( out vec4 Q, in vec2 U )
{
    Q = vec4(0);
    for (float i = -I; i <= I; i++) 
    	Q += 0.3989422804/std*A(U+vec2(0,i))*exp(-.5*i*i/std/std);

	vec4 
        n = D(U+vec2(0,1)),
        e = D(U+vec2(1,0)),
        s = D(U-vec2(0,1)),
        w = D(U-vec2(1,0));
    Q.zw  = f1*vec2(e.x-w.x,n.x-s.x);
    Q.zw -= f2*vec2(e.y-w.y,n.y-s.y);
}
