// Angular repetition
//
// Based on Inigo Quilez's articles:
// https://iquilezles.org/articles/distfunctions2d
// https://iquilezles.org/articles/distfunctions

float sdCircle( vec2 p, float r )
{
  return length(p) - r;
}

float sdLine( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

vec2 opRepAng( in vec2 p, float theta, float offset)
{
    float a = atan(p.y, p.x) - offset;
    a = mod(a + .5 * theta, theta) - .5 * theta;
    return length(p) * vec2(cos(a), sin(a));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = 1.1 * (2.0*fragCoord-iResolution.xy)/iResolution.y;

    float n = floor(2. + mod(5. * iTime, 8.));
    p = opRepAng(p, radians(360.) / n, radians(90.));

	float d = sdLine( p, vec2(0, 0), vec2(.7, 0) ) - .1;

    vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-2.0*abs(d));
	col *= 0.8 + 0.2*cos(140.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.02,abs(d)) );

	fragColor = vec4(col,1.0);
}
