float circle(vec2 coord, vec2 center, float radius) {
    float d = length((coord - center) / iResolution.y);

    return smoothstep(radius,radius - 1.5/iResolution.y, d) ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 
    float col = .0;

    for (float i = 0.; i<15.; i++) {
    	vec2 center = (vec2(.5,.5)* iResolution.xy);
        center.x += sin(.5*iTime+i)*iResolution.x*.02;
        center.y += cos(.5*iTime+i)*iResolution.x*.02;

        // how to smooth inner circle edges too?
        col += circle(fragCoord, center, 0.1+0.08*i+0.03*sin(iTime+cos(i)));
    }

    col = mod(col, 2.);
    if (col > 1.) col = 2. - col;

    // add rays from the center as well

    fragColor = vec4(vec3(col),1.0);
}
