renderpass.code:
vec2 f(in vec2 z) {
    return mat2(z,-z.y,z.x)*z-vec2(1.,0);
}
void mainImage(out vec4 O, in vec2 u) {
    vec2 z = 6.*u/iResolution.xy - 3., w = f(z);
    float s = length(w-f(z+vec2(.01,.0)))/.01;
    vec2 c = 1.-smoothstep(0.01, 0.02, abs(fract(w+.5)-.5)/s);
    vec2 p = fract(w*.5)-.5;
    O.xyz = mix(vec3(float(p.x*p.y<0.)), vec3(1,0,0), max(c.x,c.y)); O.w = 1.;
}
