#define s(a,b) length( clamp( dot(p-a,c=b-a) / dot(c,c), 0., 1. ) * c - p+a )

void mainImage( out vec4 O, vec2 u )
{
    float a = 1.05, v=a;  //  2.Pi / 6
    for( vec2 J = vec2(0,1), c,
              p =   length( c = fract(u*=4./iResolution.y)-.5 ) 
                  * abs( cos( mod(atan(c.y,c.x),a) + a + 33.*J ) ), u=u-c;
         ++a < 4.; 
         v = min(v, min( s( J.x, J/4. ),
                         s( .2* O.x*J, (O.yz + J)*.1 )
                 )      ) 
       ) O = fract(sin( ( u.x+u.y + iTime*.01 + a+vec4(J,2,0))*123. ) )+.2;
     O += 1.- v*60. -O;
}
