void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 st  = fragCoord/iResolution.y;
    vec2 uv  = fragCoord/iResolution.y;

    //these two lines create the tile pattern
    //scale the coordinate space by a certain number
    st*=4.;
    //repeat the image
    st=fract(st);

    //circle
    float pct =abs(distance(st,vec2(0.5,0.5))*5.);

    //this line adds colour
    vec3 col = 0.5 + 0.5*cos(iTime*3.+uv.xyx+vec3(0,2,6));

    col=(col/pct);

    // Output to screen
    fragColor = vec4(col,1.0);
}


//the wave pattern is beacuse st is tiled, but uv is not
