void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv *= 5.0;

    for(float i = 1.0; i < 8.0; i+= 1.0) {
    	uv.x += sin(uv.y + 2.0 + iTime) * sin(iMouse.y);
    	uv.y += cos(uv.x + 1.0 + iTime) * atan(iMouse.x);
    }

    vec3 colour = vec3(0.5 + 0.5 * tan(uv.xxy + atan(iTime) + vec3(0, 5, 10)));

    fragColor = vec4(colour,1.0);
}
