//https://www.shadertoy.com/view/XlXcW4
const uint k = 1103515245U;

vec3 hash( uvec3 x )
{
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)*(1.0/float(0xffffffffU));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uv2 = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec3 tex;
    vec2 mouse = iMouse.xy/iResolution.xy;
    if(iFrame <= 100)
        mouse += vec2(.8,.2);
	float speed = 0.0005+mouse.x*0.02;
    vec2 thing = (hash(uvec3(fragCoord, iFrame)).xy-0.5)*((iResolution.yx)*0.0002*(0.002+(pow(mouse.y,3.0))));


    tex = texture(iChannel0, uv+thing).rgb;


    if(iFrame == 0)
    {
        tex =(0.5-sin((length(uv2)-vec3(0.96,0.98,1.))*20.))*0.01;
    }
    else
    {
        tex = mix(tex,fract(vec3(mix(tex,vec3(float(tex)),0.005)-speed)*(1.0+(speed*2.))),.9);
    }

    fragColor = vec4(tex,0.);
}
