void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 offset = 1.0/iResolution.xy;
    // Output to screen
    fragColor = texture(iChannel0, uv);
}

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImageCommon( float scale, out vec4 fragColor, in vec2 fragCoord, vec3 iResolution, float iTime, sampler2D iChannel0)
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    if(iTime < 0.2){
        vec4 pattern = vec4(rand(uv + vec2(0.6,-0.2) * iTime), rand(fract(uv * iTime)), rand(uv + vec2(0.5,0.2) * iTime), 1);
    	fragColor = mix(vec4(0), pattern, float(fragCoord.x < 0.5 * iResolution.x));
    }else{
       vec4 old = texture(iChannel0, uv);
        vec2 v = normalize(old.zw - 0.7);
        mat2 M = mat2(
            v, vec2(-v.y, v.x)
        );
        M[0][0] += 0.4 * (old.x - 0.5);
        M[1][1] += 0.3 * (old.y - 0.5);
        M[1][0] += 0.02 * (old.w - 0.5);
        M[0][1] += 0.2 * (old.z - 0.5);

        vec2 offset = scale / iResolution.xy;
        fragColor = vec4(0);
        const int r = 3;

        for(int x = -r; x <= r; x++){
            for(int y = -r; y <= r; y++){
                fragColor += texture(iChannel0, (uv + vec2(x,y) * offset));
            }
        }
        fragColor /= float((2*r+1) * (2*r+1));   
        fragColor += 
            0.25 * texture(iChannel0, (uv + M * vec2(2,1) * offset)) + 
            0.25 * texture(iChannel0, (uv + M * vec2(-1,2) * offset)) + 
            0.25 * texture(iChannel0, (uv - M * vec2(2,1) * offset)) + 
            0.25 * texture(iChannel0, (uv - M * vec2(-1,2) * offset)) - 
            0.25 * texture(iChannel0, (uv - M * vec2(3,0) * offset)) - 
            0.25 * texture(iChannel0, (uv - M * vec2(0,3) * offset)) - 
            0.25 * texture(iChannel0, (uv + M * vec2(0,3) * offset)) -
            0.25 * texture(iChannel0, (uv + M * vec2(0,3) * offset));
        fragColor = clamp(fragColor,0.0,1.0);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    mainImageCommon(1.0, fragColor, fragCoord, iResolution, iTime, iChannel0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    mainImageCommon(0.9, fragColor, fragCoord, iResolution, iTime, iChannel0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    mainImageCommon(0.8, fragColor, fragCoord, iResolution, iTime, iChannel0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    mainImageCommon(0.7, fragColor, fragCoord, iResolution, iTime, iChannel0);
}
