#define PI2 6.28

float lineDF(vec2 a, vec2 b, vec2 p) {
    vec2 ap = p-a;
    vec2 ab = b-a;
    float t = clamp(dot(ap,ab)/dot(ab,ab),0.,1.);
    vec2 c = a+t*ab;
    return length(p-c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float t = iTime;

    vec2 uv = fragCoord/iResolution.xy;
	vec4 samp = texture(iChannel0, uv);

    float scale = 10.;//mix(7.,13.,sin(t*PI2/10.)*.5+.5);
	vec2 uv2 = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv2 *= scale;

    vec2 gv = fract(uv2)-.5;
    vec2 id = floor(uv2);

    float t1 = (t + (id.x*7. + id.y)*PI2)*.5;
    vec2 p = vec2(cos(t1*PI2),sin(t1*PI2))*mix(.2,.4,cos(t/3.)*.5+.5);
    float m = length(p-gv);
    float l = .1;
    float w = scale/iResolution.y;
    m = smoothstep(l+w, l-w ,m)*.2;

    samp -= mix(.003,.1,cos(t/2.)*.5+.5);
    samp += m;
    samp = clamp(samp,0.,1.);
    fragColor = samp;
}
