float circle(vec2 p,vec2 o,float r){
	return length(p - o) - r;
}
float square(vec2 p,vec2 o,float r){
	return abs(p.x-o.x) + abs(p.y - o.y) - r;
}
float dist_1(vec2 p){
	float time = fract(iTime/8.) * 8.;
    float line1 = abs(p.x) - 0.1;
    float line2 = abs(p.x - 1.0) - 0.05;
    float line3 = abs(p.x + 1.0) - 0.05;
    float wall1 = p.x + 1.75 - abs(0.5 - fract(p.y * 2.0)) * 0.5;
    float wall2 = p.x - 1.75 + abs(0.5 - fract(p.y * 2.0)) * 0.5;
    float c1 = circle(p,vec2(0.,time - 2.0),0.75);
    float c2 = circle(p,vec2(1.,time-4.),0.5);
    float c3 = circle(p,vec2(-1.,time-4.),0.5);
    float c4 = circle(p,vec2(0,time -6.),0.75);
    return min(min(wall1,-wall2),
               min(min(line1,min(line2,line3)),
            min(min(c1,c2),min(c3,c4))));

}
float dist_2(vec2 p){
    float result = 100000.0;
    for(float xi = -3.0; xi < 3.0; xi += 0.1){
        for(float yi = -3.0; yi < 3.0; yi += 0.1){
            vec2 tmp = vec2(xi+fract(iTime / 4.),yi);
        	float r = 0.05 - max(dist_1(tmp),0.00);
            result = min(result,square(p,tmp,r));
        }
    }
    return result;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
	vec3 col = dist_2(uv) > 0. ? vec3(255,69,84)/255. : vec3(0,195,227)/255.;
    fragColor = vec4(col,1.0);

}
