vec3 spectrum;
float rand(vec2 uv)
{
    return fract(sin(dot(uv, vec2(12.9898, 78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime * 1.2;
    //vec3 spectrum;

    float rep = 2.0 + pow(rand(vec2(floor(t))), 2.0) * 100.0;
    vec2 p0 = fragCoord.xy / vec2(20.0, iResolution.y / rep);
    vec2 p1 = floor(p0);
    vec2 p2 = fract(p0);

    float r = rand(p1 + floor(t) * 0.11356);
    float x = 1.0 - p2.y;
    float sp = mix(spectrum.x * 2.0, spectrum.y * 4.0, r > 0.5 ? 1.0 : 0.0);
    float th = fract(t - min(1.0, sp * (1.0 + r * 2.0)));
    float c = step(smoothstep(0.0, 1.0, th), x);

    fragColor = vec4(c);
}
