#define PI 3.1415

const int vidAmt = 4;

float greenscreen(vec3 col) {
    float g = length(col - vec3(0.,1.,0.));
    if (g > 0.63) return 1.0;
    return 0.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;

    vec4 col = vec4(0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4)), 0.1);

    for (int i = 0; i < vidAmt; i++) {
        float vidOff = float(i) / float(vidAmt) * PI*2.0;
    	vec3 vidPos = vec3(0.5 + sin(iTime + vidOff) * 0.4, 0.5, 0.5 + cos(iTime + vidOff) * 0.2);

    	vec2 vidUv = (uv / vidPos.z) - (vidPos.xy / vidPos.z - vidPos.xy);
        vec3 channelColor = texture(iChannel1, vidUv).xyz;

        if (fract(vidUv) == vidUv) {
            // col = vec4(channelColor, greenscreen(channelColor));
            col = mix(col, vec4(channelColor, 1.0), greenscreen(channelColor));
        }
    }

    fragColor = vec4(mix(col.xyz, texture(iChannel0, uv).xyz, col.a), 1.0);
}
