// Code by Flopine

// Thanks to wsmind, leon, XT95, lsdlive, lamogui, 
// Coyhot, Alkama,YX, NuSan and slerpy for teaching me

// Thanks LJ for giving me the spark :3

// Thanks to the Cookie Collective, which build a cozy and safe environment for me 
// and other to sprout :)  https://twitter.com/CookieDemoparty


#define TAU 6.2831853
#define dt(speed) fract(iTime*speed)

#define AAstep(thre, val) smoothstep(-.7,.7,(val-thre)/min(0.07,fwidth(val-thre)))
#define circle(puv,s) (length(puv)-s)


#define hash11(x) fract(sin(x)*245.3)

float frame (vec2 uv)
{
    float speed = .8;
    float r = 0.3;
    vec2 newuv = uv;
    float mask = AAstep(0.005,abs(circle(uv,r)));

    for (int i=0; i<4;i++)
    {
        newuv += vec2(cos(dt(speed)*TAU),sin(dt(speed)*TAU))*r;
        mask *= AAstep(0.01,circle(newuv,0.02));
        r = clamp(hash11(r)*0.5,0.05,1.);
        mask *= AAstep(0.005,abs(circle(newuv,r)));
        speed = clamp(hash11(speed)*0.8,0.05,1.);

    }
    mask *= AAstep(0.003, abs(newuv.x));
    mask *= AAstep(0.003, abs(newuv.y));
    return mask;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (2.*fragCoord.xy-iResolution.xy)/iResolution.y;
    vec3 col = vec3(frame(uv*1.3));
    fragColor = vec4(pow(col,vec3(0.4545)),1.0);
}
