void mainImage( out vec4 c, in vec2 p )
{
    c = vec4(dot(texture(iChannel0, p/iResolution.xy), vec4(1.)));
}

vec4 hash42(vec2 p)
{
	vec4 p4 = fract(vec4(p.xyxy) * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

vec4 t(vec2 p){return texture(iChannel0, fract(p / iResolution.xy));}

void mainImage(out vec4 c, in vec2 p )
{
    if(iFrame < 2){
       c = floor(hash42(p)+ 0.001); 
    }else{
    	vec3 o = vec3(-1, 0, 1);
        vec4 n = vec4(t(p + o.yz).x, t(p + o.zy).y, t(p + o.yx).z, t(p + o.xy).w);
        float a = 1. / 256.;
        c = max(t(p) - a, step(1. -a * .5, n));
        if(dot(c, vec4(1)) > 1.) c = iFrame % 2 < 1? c.yzwx: c.wxyz;
    }
}
