void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 mouse = iMouse.xy/iResolution.xy;
    uv -= .5;
    mouse -= .5;
	uv.x *= iResolution.x / iResolution.y;
    mouse.x *= iResolution.x / iResolution.y;
    uv *= 5.;
    mouse *= 3.;

    vec3 color;

    float t = 2.*iTime;

    vec2 p1,p2;
    p1 = vec2(-2, 0);
    p2 = vec2(2, 0);


    float v1,v2,v3,v4;
    v1 = sin(length(uv - p1)*20.-t)*exp(length(uv) + 1.);
    v2 = sin(length(uv - p2)*20.-t)*exp(length(uv) + 1.);
    v3 = sin(length(uv - mouse)*20.-t)*exp(length(uv) +1.);

    color = vec3(v1+v2+v3);

    // Output to screen
    fragColor = vec4(color, 1.0);
}
