void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 offs = fragCoord.xy - (vec2(iResolution.xy) * 0.5);		// middle of screen is 0,0
    float r = 100.0;
    float x = offs.x;
    float y = offs.y;
    float s = abs(cos(iTime * 0.5));
    s = smoothstep(0.0, 1.0, s);		// Squareness
    float v2 = x*x + y*y - s/(r*r)*x*x*y*y;
    float v = sqrt(v2);

    if (v > r || abs(x) > r)
        fragColor = vec4(0);
   	else
    {
        float q = r * 0.94;
    	float w2 = x*x + y*y - s/(q*q)*x*x*y*y;
    	float w = sqrt(w2);
        if (w <= q && abs(x) < q)
        {
            fragColor = vec4(0);
        }
 		else
        {
			fragColor = vec4(1);
        }
    }
}
