bool near(float a, float b){
	return abs(a-b)<0.1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float t = iTime;
    while(t>6.){
    	t = t - 6.;
    }
    float scale = t*10.-float(int(t*10./50.))/50.+10.;

    vec2 uv = fragCoord/iResolution.xy;
    uv = (fragCoord-0.5*iResolution.xy)/scale;

    float x= uv.x;
    float y= uv.y;

    float gray;

    if(near(sin(x*x+y*y),cos(x*y))){
        gray = 1.;
    }else{
    	gray = 0.;
    }


    float r = gray;
    float g = gray;
    float b = gray;

    vec3 col = vec3(r,g,b);
    fragColor = vec4(col,1.0);
}
