vec3 HSV_to_RGB(vec3 hsv) {

  float C = hsv.z * hsv.y;
  float X = C * (1. - abs( mod( (hsv.x * 6.),  2.) - 1.) );
  float m = hsv.z - C;

  vec3 rgb;

  switch (int(hsv.x * 6.)) {
      case 0: rgb = vec3(C,X,0); break;
      case 1: rgb = vec3(X,C,0); break;
      case 2: rgb = vec3(0,C,X); break;
      case 3: rgb = vec3(0,X,C); break;
      case 4: rgb = vec3(X,0,C); break;
      case 5: rgb = vec3(C,0,X); break;
  }

  rgb += m;

  return rgb;
}

vec3 plasma(vec2 uv) {
  // plasma
  vec2 uvc = uv - 0.5;
  uvc.x += cos(iTime) / 2.;
  uvc.y += sin(iTime) / 2.;
  vec3 hsv = vec3 (

    (cos(length(uvc*3.) * cos(uv.x*2. + iTime / 1.8) + iTime / 1.7) + 1.) / 2.1,
    1.,
    (sin(length(uvc*2.) * cos(uv.x*5. + iTime / 1.5) + iTime / 1.2) + 1.) / 2.

  );

  return hsv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  // Normalized pixel coordinates (from 0 to 1)
  vec2 uv = fragCoord/iResolution.xy;

  // discretise coordinates to make it all pixelised
  uv.x = floor(uv.x * 320.) / 320.;
  uv.y = floor(uv.y * 200.) / 200.;

  vec3 hsv = plasma(uv);

  // discretise HSV color because who need 16.777.216 colors ?
  hsv.x = floor(hsv.x * 24.) / 24.;
  hsv.y = floor(hsv.y * 16. + 16.) / 32.;
  hsv.z = floor(hsv.z * 24. + 8.) / 32.;

  fragColor = vec4(HSV_to_RGB(hsv), 1.);
}
