float noise2 (float a, float b) {
    float buff1 = fract((10000.f + abs(b + a)) * fract(a * fract((100.f + b) * fract(a * 0.0171 + b))));
    return(buff1 * 1.0038 - 0.00185) + 0.001;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    float displase = floor (iResolution.y/250.0)*5.0;
    float lenght = iResolution.y/displase;


    float sdvig = sign(fract(floor(uv.y*displase)*0.5)-0.25);
    uv.x += iTime*0.1*sdvig;

    float floory = floor(uv.y*displase);

    float pix = 16.0;
    //pix = 8.0+floory*2.0; //pixel size variation
    float fracty = fract(uv.y*displase); 
    float floorx = floor(uv.x*displase-2.5);
    if (abs(floorx)<0.1) floorx = 100.0;
    float fractx = fract(uv.x*displase);

    float fractxx = fract(fractx*2.0);
    if (fractx*2.0 < 1.0) fractxx = 1.0-fractxx;

    float flooryyy = floor(fracty*pix);
    float floorxxx = floor(fractxx*pix*0.5);

    float rand = noise2(floorxxx*(floorx)*(floory+1.0), flooryyy);

    float bright = (flooryyy)*0.1*rand;

    float color = noise2(floory+1., floorx);

    vec3 col = vec3(
    noise2(floory+1., floorx), 
    noise2(floory+15., floorx),
    noise2(floory+40., floorx)
    ) * 2.0 * bright;


    //col = vec3((flooryyy*floorxxx)*0.1*rand); // mask generator
    fragColor = vec4(col,1.0);
}
