void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5+(0.2*sin(iTime)); // set origo to be all over the place. 

    uv.x *= iResolution.x/iResolution.y; // Aspect ratio

    // angle from origo
    float a = atan(uv.y,uv.x);
	;
    // distance from origo 
    float r = length(uv);

    // inverse distance from origo
    float inv_r = 0.3/r; 

    uv = vec2( inv_r + iTime, a/3.1415927 );

    fragColor = texture(iChannel0,fract(abs(uv)),1.0);
    fragColor *= r; // dark center
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 pos1 = vec2(0.0+sin(iTime)/4.0,0.0);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5; // set center/origo to vec2(0.0,0.0);

    uv.x *= iResolution.x/iResolution.y; // Aspect ratio

    if(mod(distance(uv, pos1),0.05) > 0.025)
    {
        vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
        fragColor = vec4(col, 1.0);
    }
    else
        fragColor = vec4(0.0);



}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 pos2 = vec2(0.2*cos(iTime),-0.2*sin(iTime));

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5; // set center/origo to vec2(0.0,0.0);

    uv.x *= iResolution.x/iResolution.y; // Aspect ratio

    // Draw the second thing of top of first.
    if(mod(distance(uv, pos2),0.05) > 0.025)
    {
        vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
