void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv;

    uv = vec2(0.5 * 3.14159, 0.5 * 3.14159) + (vec2(iResolution.x / 2.0, iResolution.y / 2.0) - fragCoord.xy) / (8.0);

    float v = 25.;
    float s = 1.5;
    float t = cos(iTime / 15.);
    float value = sin(uv.x) * cos(uv.y) * ((s+v) - t * v);
    float color = sin(value) * 3.0;

    float low = abs(color);
    float med = abs(color) - 1.0;
    float high = abs(color) - 2.5;
    if(color > 0.0) {
	  fragColor = vec4(med, high, med,1.0);
    } else {
      fragColor = vec4(min(0.5, med / 2.0), high, med,1.0);
    }
}
