#define PI 3.14159265359

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // idk how to fix aspect ratio I need help
    vec2 uv = fragCoord/iResolution.y;

    float beat = max(pow(cos(.8*PI*iTime), 2.), pow(cos(.8*PI*iTime + PI*0.25), 2.));

    vec3 col = vec3(max(beat, 0.5) , 0, 0);

    float cx = 0.5;
    float cy = 0.5;

    float r = max(beat/4.5, .2);

    if (pow(pow(uv.x-cx, 2.) + pow(uv.y-cy, 2.), .5) < r) {
        fragColor = vec4(col, 1.0);
    }
    else {
        fragColor = vec4(1., 1., 1., 1.0);
    }


}
