vec3 W = vec3(0.2125, 0.7154, 0.0721);


//#define TEST


float CircleFunc(vec2 uv, vec2 center, float radius, float width )
{
    float col = 0.;

    float distFromCenter = length(uv - center );
    distFromCenter = mod( distFromCenter, radius * 2.);    
    col = smoothstep( .0, width, abs(distFromCenter- radius));

    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

#ifdef TEST
    vec3 col = vec3(uv.x );
#else
    vec3 col = texture( iChannel0, uv ).xyz;
#endif
    float luminance = dot(col, W);

    float outputLum = 1.;
    if (luminance < .9)
    {
           outputLum -= (1.- CircleFunc(uv, vec2(1.,0.), .02, .001) )*.2;
    }
    if (luminance < .75)
    {
           outputLum -= (1.- CircleFunc(uv, vec2(1.,1.), .01, .002))*.5;
    }
    if (luminance < .5)
    {
           outputLum -= (1.- CircleFunc(uv, vec2(0.,0.), .02, .013))*.8;
    }
    if (luminance < .25)
    {
           outputLum -= (1.- CircleFunc(uv, vec2(0.,1.), .01, .015));
    }

    vec3 outputColor = mix( vec3( 0.,.3,.4), vec3(1.,1.,1.), outputLum);

#ifdef TEST
    if (uv.y > .7 )
        outputColor = col;        
#endif

    fragColor = vec4(vec3(outputColor),1.0);
}
