void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 red = vec4(1.0,0.0,0.0,1.0);
    vec4 green = vec4(0.0,1.0,0.0,1.0);
    vec4 blue = vec4(0.0,0.0,1.0,1.0);
    vec2 uv = fragCoord / iResolution.xy;
    vec2 uv2 = fragCoord / iResolution.xy;
    float ratio = iResolution.x / iResolution.y;
    uv.x*=  ratio;

    vec2 center = vec2(0.5*ratio ,0.5);
    float radius = 0.3;
    if(length(uv - center) < radius ){
        fragColor=vec4(0.0);
    }
    else{
        bool odd_line = mod(uv.x+uv.y, 0.04) > 0.02;
        if(odd_line){
            fragColor = vec4(0.0);             
        }
        else{
            if(uv2.x<0.5){            
                fragColor = mix(red,green,uv2.x/0.5);
            }
            else{
                fragColor = mix(green,blue,(uv2.x-0.5)/0.5);
            }


        }

    }

}
