// variant of https://shadertoy.com/view/wt3fWr
// adapted from https://shadertoy.com/view/ttd3D7

#define D   length( mod( U += T/2. , T ) - k )               //
#define P   clamp( 1.- ( min( D, D ) -.8*k.y )/10. ,0.,1.)   // draw an hexa tiling of disks

void mainImage(out vec4 O, vec2 u) {
    O-=O;
    vec2 R = iResolution.xy,
        U = 6.*(u-R/2.),
        V =   U // * mat2(cos(.01 +vec4(0,11,33,0))) 
            + 30.* sin( U*mat2(1,-.7,.4,-1)/300. + iTime ),

        k = vec2(360)/8., T = vec2( 2, 3.5 )*k;
    O += U.x>0. ? P : 1.;

    U = 1.0*V; // try 1.1
    O *= P;
}
