#define S(a) sin(a)
#define C(a) cos(a)
#define t iTime
#define X vu.x
#define Y vu.y

float c = 0.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 vu = ( 2.* fragCoord - iResolution.xy) / iResolution.y;
	for(float i = 0.; i < 7.; i++)
	  c += abs( 1. / S( Y + 0.1 + S( i * .01 + X * 3.3 + t * .3 -i * .1 ) * C( X + t * .1 ) * S( X + t * .5 + i * .1) * C( i * .04 )  ) );
	fragColor = vec4( vec3(c) * vec3(0.003, 0.003, .01), 1. );
}
