vec2 mul(vec2 a, vec2 b) {
	return  mat2(a,-a.y,a.x) * b;
}
vec2 e (vec2 a) {
	return exp(a.x)*vec2(cos(a.y),sin(a.y));
}
vec2 ln (vec2 a) {
	return vec2(log(length(a)),atan(a.y,a.x));
}
vec2 pw (vec2 a, vec2 b) {
	return e(mul(ln(a),b));
}
vec2 pw (vec2 a, float n) {
	float f = n * atan(a.y,a.x);
    return pow(length(a),n) * vec2(cos(f),sin(f));
}
vec4 col (vec2 U) {
	#define R iResolution.xy
    float t = iTime*sqrt(iTime)/50.;
    U = (U-0.5*R)/R.y;
    U *= 1.+2./(1.-U.y);
    U *= 1000.*exp(-fract(t)*17.);
    float c = cos(iTime), s = sin(iTime);
    U *= mat2(c,-s,s,c);
    U.x += .367878;
    vec2 z = U;
    float o = mod(floor(t),5.);
    float n = 2.6;
    if (o==1.) n = 1.7;
    if (o==2.) n = -2.1;
    if (o==3.) n = -2.8;
    if (o==4.) n = 2.;
    n += .3*sin(1243.*floor(t));
    for (float i = 0.; i < 20.; i++) {
        if (length(z)==0.) return vec4(0);
    	z = ln(z);
        z = pw(z,n);
        if (length(z)>1e4) return vec4(1);
        z = e(z);
    }
   	return min(abs(z.xyxy),1.);
}
void mainImage( out vec4 Q, in vec2 U )
{
    Q = 0.25*(
        col(U+vec2(.5,0))+
        col(U+vec2(0,.5))+
        col(U-vec2(.5,0))+
        col(U-vec2(0,.5))
    )*exp(-.5*length(U-0.5*R)/R.y);
}
