vec4 backgroundColor = vec4(0,0,0,0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	uint radius = uint(50000.0 + 25000.0*sin(iTime)); //pixels 

	vec4 circleColor = vec4(sin(iTime)*0.5 + sin(iTime/10.0)*0.5,cos(iTime),1,0);
    uvec2 coord = uvec2(fragCoord * 1000.0);
    uvec2 center = uvec2(iResolution.xy/2.0 * 1000.0); 
    uvec2 diff = coord - center;
    if(diff.x * diff.x + diff.y*diff.y < radius * radius)
        fragColor = circleColor;
    else
        fragColor = backgroundColor;
}
