void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 pos = vec2(uv.x*20.0-10.0,uv.y*20.0-10.0);
    float x = pos.x;
    float y = pos.y*0.6;
    vec2 eyeOffset = vec2(-0.2,-1.5);
    float open = sin(iTime*10.0) * 0.2 + 1.2;
    float value = ((x*x + y*y) < 11.0 && atan(y,x)<3.14*0.8*open && atan(y,x)>-3.14*0.8*open &&
                   ((x+eyeOffset.x)
                     *(x+eyeOffset.x)+(y+eyeOffset.y)*(y+eyeOffset.y))>0.2)

        ? 1.0 : 0.0;
    fragColor = vec4(value,value,0.0,1.0);
}
