vec2 str(vec2 x)
{
	float a = distance(vec2(0.5), x);
    float c = sqrt(a);
    vec2 f = (vec2(c,c))/1.0;
    vec2 g = clamp(smoothstep(0.65, 0.0, f),0.,1.);
    return g;
}

float rand(vec2 cor){
    return fract(sin(dot(cor.xy ,vec2(sin(iTime*cor.x/50.),.2))));
}

vec3 ab(vec2 uvN, vec3 c)
{
    for(int i=1; i<10; i++)
    {
        float x = rand(uvN+sin(uvN.x*iTime*3.));
        float y = rand(uvN+cos(uvN.x*iTime*2.));
        float z = rand(uvN+tan(uvN.x*iTime));
        vec3 k = vec3(x,y,z);
        c *= k;
    }
    return c;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord/iResolution.xy;

	vec3 col = vec3(1,1,1);

    col = ab(str(uv),col)*str(uv).x;

    fragColor = vec4(col,1.0);
}
