 float disk(float x, float y, float r) {
    return 1. - step(r * r, x * x + y * y);
 }

 void mainImage(out vec4 fragColor, in vec2 fragCoord ) {

     vec2 uv = fragCoord/iResolution.xy - 0.5;
    // CREATE SIX ANIMATING DISKS.

    float c = 0.0;
    for (int i = 0 ; i < 6 ; i++) {
       float t = float(i);
       float x = uv.x - 0.5 * sin(2.0 * iTime + t);
       float y = uv.y + 0.8*sin(iTime) * sin(2.7 * iTime + t);
       c = c + disk(x, y, 0.1);
    }

    vec3 color = vec3(c, c, c); 
    // Output to screen
    fragColor = vec4(sqrt(color),1.0);
}
